svg.script:
// // this is to make javascript work in the SVG, leave it alone
    function updateSVG(instrumentValue){
    // ========================================
    // STUDENT EDIT ZONE
    // This section controls how the instrument displays
    // ========================================
    /**
     * Updates the elevator shape display
     * - The elevator-shape element rotates to show elevator control position
     * - -100 = -30 degrees rotation (rotated counter-clockwise), RED color
     * - 0 = 0 degrees rotation (neutral position), BLACK color
     * - +100 = +30 degrees rotation (rotated clockwise), GREEN color
     * - Color interpolates proportionally between these values
     */
      // Calculate rotation angle
      // Formula: (value / 100) * 30 to map -100 to +100 to -30 to +30 degrees
      const angle = (instrumentValue / 100) * 30 *-1;

      // Get the elevator-shape element
      const elevatorShape = document.getElementById('elevator-shape');
      if (!elevatorShape) {
        console.log('Cannot find elevator-shape element to animate');
        return;
      }

      // Calculate color based on value
      // Normalize value from -100 to +100 into 0 to 1 range
      const normalized = (instrumentValue + 100) / 200;

      let r, g, b;
      if (normalized <= 0.5) {
        // From -100 (red) to 0 (black): interpolate red to black
        // At -100 (normalized=0): red (255, 0, 0)
        // At 0 (normalized=0.5): black (0, 0, 0)
        const t = normalized * 2; // 0 to 1 within this range
        r = Math.round(255 * (1 - t));
        g = 0;
        b = 0;
      } else {
        // From 0 (black) to +100 (green): interpolate black to green
        // At 0 (normalized=0.5): black (0, 0, 0)
        // At +100 (normalized=1): green (22, 236, 0)
        const t = (normalized - 0.5) * 2; // 0 to 1 within this range
        r = 0;
        g = Math.round(22 + (236 - 22) * t);
        b = 0;
      }

      const color = `rgb(${r}, ${g}, ${b})`;
      elevatorShape.style.fill = color;

      // Center point of the elevator-shape for rotation
      // The shape is centered around x=256, y=366
      const centerX = 256;
      const centerY = 366;

      // Rotate the elevator-shape around its center point
      elevatorShape.setAttribute('transform', `rotate(${angle}, ${centerX}, ${centerY})`);

    // ========================================
    // END STUDENT EDIT ZONE
    // ========================================
    }

    setInterval(() => { // do this repeatedly

    // Check localStorage for updates
      const thisSVGfilename = window.location.pathname;
    // this SVG expects a localStorage with the same name as its file
      const localStorageName =  thisSVGfilename.substring(thisSVGfilename.lastIndexOf('/') + 1, thisSVGfilename.lastIndexOf('.'));
      let instrumentValue = localStorage.getItem(localStorageName);

    if (instrumentValue !== null) {
      // Make sure value is between -100 and +100
      instrumentValue = Math.max(-100, Math.min(100, instrumentValue));

    } else {
    // no localStorage, so make up a number, so it swings back and forth
    // Initialize static variables if they don't exist
    if (typeof window.testValue === 'undefined') {
      window.testValue = -100;
      window.testDirection = 1; // 1 for increasing, -1 for decreasing
    }

    // Update the test value
    window.testValue += window.testDirection * 2; // increment by 2 each time

    // Reverse direction at boundaries
    if (window.testValue >= 100) {
      window.testValue = 100;
      window.testDirection = -1;
    } else if (window.testValue <= -100) {
      window.testValue = -100;
      window.testDirection = 1;
    }

    instrumentValue = window.testValue;
    console.log('no local storage "'+localStorageName+'", using test value:', instrumentValue);

     }

    updateSVG(instrumentValue)

    }, 50);  //end of setInterval code.  50 is 50 milliseconds or 20 times per second.

  // // this is to make javascript work in the SVG, leave it alone
