svg.script:
window.updateInstrument = function(degrees) {
  const indicator = document.getElementById('indicator');
  if (!indicator) return;

  // Clamp pitch between -25 and +25 degrees
  const normalizedPitch = Math.max(-25, Math.min(25, degrees));

  // Map -25 to +25 degrees to x position 50 to 462
  // Center (0째) is at x=256
  const xPosition = 256 + (normalizedPitch / 25) * 206;

  indicator.setAttribute('x1', xPosition);
  indicator.setAttribute('x2', xPosition);
};
