svg.script:
window.updateInstrument = function(degrees) {
  const indicator = document.getElementById('indicator');
  if (!indicator) return;

  // Clamp pitch between -45 and +45 degrees
  const normalizedPitch = Math.max(-45, Math.min(45, degrees));

  // Map -45 to +45 degrees to x position 50 to 462
  // Center (0째) is at x=256
  const xPosition = 256 + (normalizedPitch / 45) * 206;

  indicator.setAttribute('x1', xPosition);
  indicator.setAttribute('x2', xPosition);
};
